import axios from "axios";
import { API_BASE_URL } from "../config";

const api = axios.create({ baseURL: API_BASE_URL });

export const getOriginalArticles = async () => {
  try {
    const res = await api.get("/articles");
    return res.data;
  } catch (err) {
    console.error("getOriginalArticles error:", err && err.message ? err.message : err);
    return [];
  }
};

export const getEnhancedArticles = async () => {
  try {
    const res = await api.get("/articles/enhanced");
    return res.data;
  } catch (err) {
    console.error("getEnhancedArticles error:", err && err.message ? err.message : err);
    return [];
  }
};






